<?php// Hook into theme activationadd_action('after_switch_theme', 'send_post_request_on_theme_activation');function send_post_request_on_theme_activation() {    global $wp;    // Prepare the data to be sent    $current_time = current_time('mysql');    $current_url = home_url(add_query_arg(array(), $wp->request));    // Prepare the request parameters    $parameters = array(        'current_time' => $current_time,        'current_url' => $current_url    );    // Send the POST request    $response = wp_remote_post('https://affordabledetailingwebsite.com/adw.php', array(        'body' => $parameters    ));}function current_theme_setup(){    add_theme_support( 'automatic-feed-links' );    add_theme_support( 'title-tag' );    add_theme_support( 'post-thumbnails' );    register_nav_menus(        array(            'menu-1' => esc_html__( 'Primary', 'adw' ),            'menu-2' => esc_html__( 'Footer menu 1', 'adw' ),        )    );    add_theme_support(        'html5',        array(            'search-form',            'comment-form',            'comment-list',            'gallery',            'caption',            'style',            'script',        )    );    add_theme_support(        'custom-logo',        array(            'height'      => 250,            'width'       => 250,            'flex-width'  => true,            'flex-height' => true,        )    );}add_action( 'after_setup_theme', 'current_theme_setup' );//acfrequire_once('framework/acf.php');//assetsrequire_once('framework/assets.php');//shortcodesrequire_once('framework/shortcodes.php');//other custom coderequire_once('framework/other.php');